import TaskForm from "./app_components/TaskForm";
import Task1 from "./app_components/Task1";
import { useEffect, useState } from "react";
import TaskFacade from "./app_components/TaskFacade";
import TaskListProxy from "./app_components/TaskListProxy";
import TaskFilterStrategyFactory from './app_components/TaskFilterStrategyFactory';
import MediumPriorityState from "./app_components/State/MediumPriorityState";
import LowPriorityState from "./app_components/State/LowPriorityState";
import HighPriorityState from "./app_components/State/HighPriorityState";
import TaskBuilder from "./app_components/TaskBuilder";
import { AllTasksFilter } from "./app_components/FilterStrategy";

const App = () => {
    const [tasks, setTasks] = useState([]);
    const [filter, setFilter] = useState("toate");
    const [priorityFilter, setPriorityFilter] = useState("toate");

    const filterStrategyFactory = new TaskFilterStrategyFactory();
    const filterStrategy = filterStrategyFactory.createFilterStrategy(filter);

    const filterTasks = (tasks) => {
        return filterStrategy.filter(tasks);
    };

    const filterPriority = (tasks) => {
        if (priorityFilter === "toate") {
            return tasks.map((task) => ({...task, priorityClass: ""}));
        } else {
            let priorityStrategy;
            if (priorityFilter === "urgent") {
                priorityStrategy = new HighPriorityState();
            } else if (priorityFilter === "mediu") {
                priorityStrategy = new MediumPriorityState();
            } else if (priorityFilter === "scazut") {
                priorityStrategy = new LowPriorityState();
            } else {
                priorityStrategy = new AllTasksFilter();
            }
            return tasks.map((task) => ({
                ...task,
                priorityClass: priorityStrategy.includes(task) ? task.priority : "",
            }));
        }
    };

    useEffect(() => {
        if (tasks.length === 0) return;
        localStorage.setItem("tasks", JSON.stringify(tasks));
    }, [tasks]);

    useEffect(() => {
        const savedTasks = JSON.parse(localStorage.getItem("tasks"));
        setTasks(savedTasks || []);
    }, []);

    const taskBuilder = new TaskBuilder();
    const taskFacade = new TaskFacade(taskBuilder);
    const taskListProxy = new TaskListProxy();

    const addTask = (name, priority) => {
        const task = taskFacade.buildTask(name, priority);
        taskListProxy.addTask(task);
        setTasks(taskListProxy.getTasks());
    };

    const removeAllTasks = () => {
        taskListProxy.removeAllTasks();
        setTasks(taskListProxy.getTasks());
    };

    const removeTask = (indexToRemove) => {
        taskListProxy.removeTask(indexToRemove);
        setTasks(taskListProxy.getTasks());
    };

    const updateTaskDone = (taskIndex, newDone) => {
        taskListProxy.updateTaskDone(taskIndex, newDone);
        setTasks(taskListProxy.getTasks());
    };

    const renameTask = (index, newName) => {
        taskListProxy.renameTask(index, newName);
        setTasks(taskListProxy.getTasks());
    };

    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const handlePriorityFilterChange = (event) => {
        setPriorityFilter(event.target.value);
    };

    return (
        <main>
            <h1>LISTĂ DE ACTIVITĂȚI</h1>
            <div className="filter">
                <label htmlFor="filterSelect">Filtrare: </label>
                <select id="filterSelect" value={filter} onChange={handleFilterChange}>
                    <option value="toate">Toate</option>
                    <option value="finalizate">Finalizate</option>
                    <option value="nefinalizate">Nefinalizate</option>
                </select>
            </div>
            <div className="filter">
                <label htmlFor="priorityFilterSelect">Filtrare după prioritate: </label>
                <select id="priorityFilterSelect" value={priorityFilter} onChange={handlePriorityFilterChange}>
                    <option value="toate">Toate</option>
                    <option value="urgent">Urgent</option>
                    <option value="mediu">Mediu</option>
                    <option value="scazut">Scăzut</option>
                </select>
            </div>
            <TaskForm onAdd={addTask}/>
            {filterPriority(filterTasks(tasks)).map((task, index) => (
                <Task1
                    key={index}
                    {...task}
                    onRename={(newName) => renameTask(index, newName)}
                    onTrash={() => removeTask(index)}
                    onToggle={(done) => updateTaskDone(index, done)}
                />
            ))}
            <button onClick={removeAllTasks}>Șterge toate</button>
        </main>
    )
}
