const Task = ({ name, done, priority, priorityClass, onRename, onTrash, onToggle }) => {
    const handleCheckboxChange = () => {
        onToggle(!done);
    };

    const handleInputChange = (event) => {
        onRename(event.target.value);
    };

    return (
        <div className={`task ${done ? "done" : ""}`}>
            <div className="checkbox">
                <input type="checkbox" checked={done} onChange={handleCheckboxChange} />
                <span className={`task-name ${priorityClass}`}>{name}</span>
            </div>
            <form>
                <input type="text" value={name} onChange={handleInputChange} />
                <button className="trash" onClick={onTrash}>
                    <TrashIcon />
                </button>
            </form>
        </div>
    );
};
