// TaskListProxy.js
class TaskListProxy {
    constructor(tasks) {
        this.tasks = tasks;
        this.modifiedTasks = [];
    }

    getTasks() {
        return [...this.tasks, ...this.modifiedTasks];
    }

    addTask(task) {
        this.modifiedTasks.push(task);
    }

    removeTask(indexToRemove) {
        this.modifiedTasks = this.modifiedTasks.filter((taskObject, index) => index !== indexToRemove);
    }

    // Alte metode relevante pentru gestionarea task-urilor

    commitChanges() {
        this.tasks.push(...this.modifiedTasks);
        this.modifiedTasks = [];
    }
}
